import React, { useEffect, useState } from "react";
import Chart from "chart.js/auto";

const TemperatureChart = () => {
  const [temperatureData, setTemperatureData] = useState(null);

  useEffect(() => {
    // Truy vấn API hoặc dịch vụ dự báo thời tiết ở đây
    // Đoạn mã này chỉ là một ví dụ giả định

    // Mock data
    const mockData = {
      labels: ["23h", "00h", "1h", "2h", "3h"],
      datasets: [
        {
          label: "Temperature",
          data: [29, 28, 28, 29, 29], // Dữ liệu nhiệt độ (ví dụ)
          borderColor: "rgba(255, 99, 132, 1)",
          backgroundColor: "rgba(255, 99, 132, 0.2)",
        },
      ],
    };

    setTemperatureData(mockData); // Cập nhật state với dữ liệu giả định
  }, []);

  useEffect(() => {
    // Vẽ biểu đồ khi weatherData có dữ liệu
    if (temperatureData) {
      const ctx = document.getElementById("temperatureChart").getContext("2d");
      new Chart(ctx, {
        type: "line",
        data: temperatureData,
        options: {
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        },
      });
    }
  }, [temperatureData]);

  return (
    <div className="chartContainer">
      <canvas id="temperatureChart"></canvas>
    </div>
  );
};

export default TemperatureChart;
