import React, { useEffect, useState } from "react";
import Chart from "chart.js/auto";

const RainChart = () => {
  const [rainData, setRainData] = useState(null);

  useEffect(() => {
    // Truy vấn API hoặc dịch vụ dự báo thời tiết ở đây
    // Đoạn mã này chỉ là một ví dụ giả định

    // Mock data
    const mockData = {
      labels: ["23h", "00h", "1h", "2h", "3h"],
      datasets: [
        {
          label: "Rain",
          data: [40, 20, 10, 20, 20], // Dữ liệu lượng mưa (ví dụ)
          borderColor: "rgba(255, 99, 132, 1)",
          backgroundColor: "rgba(255, 99, 132, 0.2)",
        },
      ],
    };

    setRainData(mockData); // Cập nhật state với dữ liệu giả định
  }, []);

  useEffect(() => {
    // Vẽ biểu đồ khi humidityData có dữ liệu
    if (rainData) {
      const ctx = document.getElementById("rainChart").getContext("2d");
      new Chart(ctx, {
        type: "line",
        data: rainData,
        options: {
          scales: {
            y: {
              beginAtZero: true,
            },
          },
        },
      });
    }
  }, [rainData]);

  return (
    <div className="chartContainer">
      <canvas id="rainChart"></canvas>
    </div>
  );
};

export default RainChart;
